import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
const API_URL = process.env.REACT_APP_API_URL;

function ResetPassword() {
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [showMessageModal, setShowMessageModal] = useState(false);

  const navigate = useNavigate();

  const handleReset = (e) => {
    e.preventDefault();
    setLoading(true);

    fetch(`${API_URL}/api/auth/reset-password`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email }),
    })
      .then(async res => {
        if (!res.ok) {
          const errorData = await res.json().catch(() => ({}));
          throw new Error(errorData.message || 'Failed to send reset email');
        }
        return res.json();
      })
      .then(() => {
        setMessage("Password reset link sent to your email!");
        setShowMessageModal(true);
        setEmail('');
        setLoading(false);
      })
      .catch(err => {
        setMessage(err.message);
        setShowMessageModal(true);
        setLoading(false);
      });
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100" style={{ background: 'linear-gradient(135deg,rgb(77, 158, 202), #0d6efd)' }}>
      <div className="card shadow-lg p-4 rounded-4" style={{ width: '400px', backgroundColor: '#ffffff' }}>
        <h2 className="text-center mb-4 fw-bold text-primary">ðŸ”‘ Reset Password</h2>
        <form onSubmit={handleReset}>
          <div className="mb-3">
            <input
              type="email"
              className="form-control form-control-lg"
              placeholder="Enter your email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
              disabled={loading}
            />
          </div>
          <button type="submit" className="btn btn-primary w-100 btn-lg fw-bold" disabled={loading}>
            {loading ? 'Processing...' : 'Send Reset Link'}
          </button>
        </form>

        <div className="text-center mt-3">
          <Link to="/login" className="text-decoration-none text-primary fw-bold">
            Back to Login
          </Link>
        </div>

        {/* Message Modal */}
        {showMessageModal && (
          <div className="modal fade show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
            <div className="modal-dialog modal-dialog-centered">
              <div className="modal-content border-primary rounded-4">
                <div className="modal-body text-center">
                  <p>{message}</p>
                  <button className="btn btn-primary" onClick={() => setShowMessageModal(false)}>OK</button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default ResetPassword;
