import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from "react-router-dom";
import App from "./App"; // Course Management
import StudentRegistration from "./StudentRegistration";
import MarkManagement from "./MarkManagement";
import Login from "./Login";
import Register from "./Register";
import "./Main.css";

// âœ… Protected Route wrapper
const PrivateRoute = ({ children }) => {
  const user = JSON.parse(localStorage.getItem("user"));
  return user ? children : <Navigate to="/login" />;
};

// âœ… Navbar for authenticated users only
const Navbar = () => (
  <nav
    className="navbar px-4 shadow-sm"
    style={{
      background: "linear-gradient(90deg, #0d6efd, #6610f2)",
      paddingTop: "0.75rem",
      paddingBottom: "0.75rem",
    }}
  >
    <div className="container-fluid d-flex align-items-center justify-content-between">
      <span className="navbar-brand text-white fw-bold fs-4">
        ðŸŽ“ Uni Dashboard
      </span>

      <div className="btn-group">
        <Link to="/" className="btn nav-btn me-2 btn-outline-light text-white">
          Course Management
        </Link>
        <Link
          to="/students"
          className="btn nav-btn me-2 btn-outline-light text-white"
        >
          Student Registration
        </Link>
        <Link
          to="/marks"
          className="btn nav-btn btn-outline-light text-white"
        >
          Marks Management
        </Link>
      </div>
    </div>
  </nav>
);

function Main() {
  const user = JSON.parse(localStorage.getItem("user"));

  return (
    <Router>
      <Routes>
        {/* Public routes */}
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* Protected routes */}
        <Route
          path="/"
          element={
            <PrivateRoute>
              {user && <Navbar />}
              <App />
            </PrivateRoute>
          }
        />
        <Route
          path="/students"
          element={
            <PrivateRoute>
              {user && <Navbar />}
              <StudentRegistration />
            </PrivateRoute>
          }
        />
        <Route
          path="/marks"
          element={
            <PrivateRoute>
              {user && <Navbar />}
              <MarkManagement />
            </PrivateRoute>
          }
        />

        {/* Redirect any unknown route */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default Main;
