import React, { useState, useEffect } from 'react';

function StudentRegistration() {
  const [students, setStudents] = useState([]);
  const [studentId, setStudentId] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [course, setCourse] = useState('');
  const [loading, setLoading] = useState(false);
  const [editingId, setEditingId] = useState(null);

  // Modals state
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [deleteStudentId, setDeleteStudentId] = useState(null);

  const [showMessageModal, setShowMessageModal] = useState(false);
  const [message, setMessage] = useState('');

  // Fetch students from backend
  const fetchStudents = () => {
    setLoading(true);
    fetch('http://localhost:8080/api/students')
      .then(res => {
        if (!res.ok) throw new Error('Failed to fetch students');
        return res.json();
      })
      .then(data => {
        setStudents(Array.isArray(data) ? data : []);
        setLoading(false);
      })
      .catch(err => {
        setMessage('Error loading students: ' + err.message);
        setShowMessageModal(true);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  // Reset form and editing state
  const resetForm = () => {
    setStudentId('');
    setName('');
    setEmail('');
    setCourse('');
    setEditingId(null);
  };

  // Handle form submit for add or edit
  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8080/api/students/${editingId}`
      : 'http://localhost:8080/api/students';

    fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ studentId, name, email, course }),
    })
      .then(res => {
        if (!res.ok) throw new Error('Failed to save student');
        return res.json();
      })
      .then(() => {
        fetchStudents();
        resetForm();
        setMessage(editingId ? 'Student updated successfully!' : 'Student registered successfully!');
        setShowMessageModal(true);
        setLoading(false);
      })
      .catch(err => {
        setMessage('Error: ' + err.message);
        setShowMessageModal(true);
        setLoading(false);
      });
  };

  // Fill form for editing
  const handleEdit = (student) => {
    setEditingId(student.id);
    setStudentId(student.studentId);
    setName(student.name);
    setEmail(student.email);
    setCourse(student.course);
  };

  // Open delete confirmation modal
  const openDeleteModal = (id) => {
    setDeleteStudentId(id);
    setShowDeleteModal(true);
  };

  // Confirm delete student
  const confirmDelete = () => {
    setLoading(true);
    fetch(`http://localhost:8080/api/students/${deleteStudentId}`, {
      method: 'DELETE',
    })
      .then(res => {
        if (!res.ok) throw new Error('Failed to delete student');
        fetchStudents();
        setShowDeleteModal(false);
        setMessage('Student deleted successfully!');
        setShowMessageModal(true);
        setLoading(false);
      })
      .catch(err => {
        setShowDeleteModal(false);
        setMessage('Error deleting student: ' + err.message);
        setShowMessageModal(true);
        setLoading(false);
      });
  };

  return (
    <div
      className="container mt-5 p-4 rounded shadow"
      style={{ backgroundColor: '#f8f9fa', minHeight: '90vh' }}
    >
      <div className="bg-dark text-white py-3 px-4 rounded-3 shadow mb-4 text-center">
        <h2 className="m-0 fw-bold">👨‍🎓 Student Registration</h2>
      </div>

      {/* Form card */}
      <div className="card mb-4 shadow-sm border-primary">
        <div className="card-body">
          <h5 className="card-title text-primary mb-4">{editingId ? '✏️ Edit Student' : '➕ Register New Student'}</h5>
          <form onSubmit={handleSubmit} className="px-1">
            <div className="row g-3">
              <div className="col-md-3">
                <div className="form-floating">
                  <input
                    id="studentId"
                    type="text"
                    className="form-control"
                    placeholder="Student ID"
                    value={studentId}
                    onChange={e => setStudentId(e.target.value)}
                    required
                    disabled={loading}
                  />
                  <label htmlFor="studentId">Student ID</label>
                </div>
              </div>

              <div className="col-md-3">
                <div className="form-floating">
                  <input
                    id="name"
                    type="text"
                    className="form-control"
                    placeholder="Full Name"
                    value={name}
                    onChange={e => setName(e.target.value)}
                    required
                    disabled={loading}
                  />
                  <label htmlFor="name">Full Name</label>
                </div>
              </div>

              <div className="col-md-3">
                <div className="form-floating">
                  <input
                    id="email"
                    type="email"
                    className="form-control"
                    placeholder="Email"
                    value={email}
                    onChange={e => setEmail(e.target.value)}
                    required
                    disabled={loading}
                  />
                  <label htmlFor="email">Email</label>
                </div>
              </div>

              <div className="col-md-3">
                <div className="form-floating">
                  <input
                    id="course"
                    type="text"
                    className="form-control"
                    placeholder="Course"
                    value={course}
                    onChange={e => setCourse(e.target.value)}
                    disabled={loading}
                  />
                  <label htmlFor="course">Course</label>
                </div>
              </div>
            </div>

            <div className="mt-4 text-end">
              {editingId && (
                <button
                  type="button"
                  className="btn btn-danger me-2"
                  onClick={resetForm}
                  disabled={loading}
                >
                  Cancel
                </button>
              )}
              <button type="submit" className={`btn ${editingId ? 'btn-warning' : 'btn-primary'}`} disabled={loading}>
                {loading ? 'Processing...' : editingId ? 'Update Student' : 'Register Student'}
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Students table */}
      <div className="card shadow-sm border-primary rounded-4">
        <div className="card-body">
          <h4 className="card-title mb-4 text-primary fw-bold">📋 Student List</h4>
          <div className="table-responsive rounded-4 overflow-hidden border">
            <table className="table table-hover table-bordered align-middle mb-0">
              <thead className="text-dark table-primary text-center">
                <tr>
                  <th className="fw-semibold">Student ID</th>
                  <th className="fw-semibold">Name</th>
                  <th className="fw-semibold">Email</th>
                  <th className="fw-semibold">Course</th>
                  <th className="fw-semibold">Registration Date</th>
                  <th className="fw-semibold">Actions</th>
                </tr>
              </thead>
              <tbody>
                {loading && students.length === 0 ? (
                  <tr>
                    <td colSpan="6" className="text-center">
                      Loading students...
                    </td>
                  </tr>
                ) : students.length === 0 ? (
                  <tr>
                    <td colSpan="6" className="text-center text-muted">
                      No students found.
                    </td>
                  </tr>
                ) : (
                  students.map(student => (
                    <tr key={student.id}>
                      <td className="text-center">{student.studentId}</td>
                      <td>{student.name}</td>
                      <td>{student.email}</td>
                      <td>{student.course}</td>
                      <td className="text-center">{student.registration_date || student.registrationDate}</td>
                      <td className="text-center">
                        <div className="d-flex justify-content-center gap-2 flex-wrap">
                          <button
                            className="btn btn-sm btn-primary"
                            onClick={() => handleEdit(student)}
                            disabled={loading}
                            title="Edit Student"
                          >
                            Edit
                          </button>
                          <button
                            className="btn btn-sm btn-danger"
                            onClick={() => openDeleteModal(student.id)}
                            disabled={loading}
                            title="Delete Student"
                          >
                            Delete
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Delete Confirmation Modal */}
      <div
        className={`modal fade ${showDeleteModal ? 'show d-block' : ''}`}
        tabIndex="-1"
        style={{ backgroundColor: showDeleteModal ? 'rgba(0,0,0,0.5)' : 'transparent' }}
        aria-modal={showDeleteModal ? 'true' : 'false'}
        role="dialog"
      >
        <div className="modal-dialog modal-dialog-centered">
          <div className="modal-content border-danger">
            <div className="modal-header bg-danger text-white">
              <h5 className="modal-title">Confirm Delete</h5>
              <button
                type="button"
                className="btn-close btn-close-white"
                onClick={() => setShowDeleteModal(false)}
              />
            </div>
            <div className="modal-body">Are you sure you want to delete this student?</div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => setShowDeleteModal(false)}
                disabled={loading}
              >
                Cancel
              </button>
              <button
                type="button"
                className="btn btn-danger"
                onClick={confirmDelete}
                disabled={loading}
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Message Modal */}
      <div
        className={`modal fade ${showMessageModal ? 'show d-block' : ''}`}
        tabIndex="-1"
        style={{ backgroundColor: showMessageModal ? 'rgba(0,0,0,0.5)' : 'transparent' }}
        aria-modal={showMessageModal ? 'true' : 'false'}
        role="dialog"
      >
        <div className="modal-dialog modal-dialog-centered">
          <div className="modal-content border-primary">
            <div className="modal-body text-center">
              <p>{message}</p>
              <button
                type="button"
                className="btn btn-primary"
                onClick={() => setShowMessageModal(false)}
              >
                OK
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default StudentRegistration;
