import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [courses, setCourses] = useState([]);
  const [title, setTitle] = useState('');
  const [code, setCode] = useState('');
  const [description, setDescription] = useState('');
  const [credits, setCredits] = useState('');
  const [editingId, setEditingId] = useState(null);

  // Modal controls
  const [showFormModal, setShowFormModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [deleteCourseId, setDeleteCourseId] = useState(null);
  const [message, setMessage] = useState('');
  const [showMessageModal, setShowMessageModal] = useState(false);

  // Fetch all courses
  const fetchCourses = () => {
    fetch("http://localhost:8080/api/courses")
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setCourses(data);
        } else {
          setCourses([]);
        }
      });
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  // Open Add Course Modal
  const openAddModal = () => {
    setEditingId(null);
    setCode('');
    setTitle('');
    setDescription('');
    setCredits('');
    setShowFormModal(true);
  };

  // Open Edit Course Modal
  const openEditModal = (course) => {
    setEditingId(course.id);
    setCode(course.code);
    setTitle(course.title);
    setDescription(course.description);
    setCredits(course.credits);
    setShowFormModal(true);
  };

  // Submit Add or Edit
  const handleSubmit = () => {
    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8080/api/courses/${editingId}`
      : `http://localhost:8080/api/courses`;

    fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ code, title, description, credits })
    })
      .then(res => {
        if (!res.ok) throw new Error('Failed to save course');
        return res.json();
      })
      .then(() => {
        fetchCourses();
        setShowFormModal(false);
        setMessage(editingId ? 'Course updated successfully!' : 'Course added successfully!');
        setShowMessageModal(true);
      })
      .catch(() => {
        setMessage('Something went wrong. Please try again.');
        setShowMessageModal(true);
      });
  };

  // Open Delete Confirmation Modal
  const openDeleteModal = (id) => {
    setDeleteCourseId(id);
    setShowDeleteModal(true);
  };

  // Confirm Delete
  const confirmDelete = () => {
    fetch(`http://localhost:8080/api/courses/${deleteCourseId}`, {
      method: 'DELETE'
    })
      .then(res => {
        if (!res.ok) throw new Error('Failed to delete course');
        fetchCourses();
        setShowDeleteModal(false);
        setMessage('Course deleted successfully!');
        setShowMessageModal(true);
      })
      .catch(() => {
        setShowDeleteModal(false);
        setMessage('Failed to delete course. Please try again.');
        setShowMessageModal(true);
      });
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-center">University Course Management</h2>

      <div className="mb-3 text-center">
        <button className="btn btn-primary" onClick={openAddModal}>
          + Add New Course
        </button>
      </div>

      {/* Courses Table */}
      <div className="card shadow-sm">
        <div className="card-body">
          <h4 className="card-title mb-3">ðŸ“‹ Course List</h4>
          <div className="table-responsive">
            <table className="table table-striped table-hover align-middle">
              <thead className="table-dark">
                <tr>
                  <th>Code</th>
                  <th>Title</th>
                  <th>Description</th>
                  <th>Credits</th>
                  <th className="text-center">Actions</th>
                </tr>
              </thead>
              <tbody>
                {courses.length === 0 ? (
                  <tr>
                    <td colSpan="5" className="text-center text-muted">No courses found.</td>
                  </tr>
                ) : (
                  courses.map(course => (
                    <tr key={course.id}>
                      <td>{course.code}</td>
                      <td>{course.title}</td>
                      <td>{course.description}</td>
                      <td>{course.credits}</td>
                      <td className="text-center">
                        <button
                          className="btn btn-sm btn-outline-primary me-2"
                          onClick={() => openEditModal(course)}
                        >
                          Edit
                        </button>
                        <button
                          className="btn btn-sm btn-outline-danger"
                          onClick={() => openDeleteModal(course.id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Add/Edit Modal */}
      <div
        className={`modal fade ${showFormModal ? 'show d-block' : ''}`}
        tabIndex="-1"
        style={{ backgroundColor: showFormModal ? 'rgba(0,0,0,0.5)' : 'transparent' }}
        aria-modal={showFormModal ? "true" : "false"}
        role="dialog"
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">{editingId ? 'Edit Course' : 'Add New Course'}</h5>
              <button
                type="button"
                className="btn-close"
                onClick={() => setShowFormModal(false)}
              ></button>
            </div>
            <div className="modal-body">
              <form
                onSubmit={e => {
                  e.preventDefault();
                  handleSubmit();
                }}
              >
                <div className="mb-3">
                  <label className="form-label">Course Code</label>
                  <input
                    type="text"
                    className="form-control"
                    value={code}
                    onChange={e => setCode(e.target.value)}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Course Title</label>
                  <input
                    type="text"
                    className="form-control"
                    value={title}
                    onChange={e => setTitle(e.target.value)}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Credits</label>
                  <input
                    type="number"
                    className="form-control"
                    value={credits}
                    onChange={e => setCredits(e.target.value)}
                    min="0"
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Course Description</label>
                  <textarea
                    className="form-control"
                    rows="3"
                    value={description}
                    onChange={e => setDescription(e.target.value)}
                    required
                  ></textarea>
                </div>
                <div className="text-end">
                  <button
                    type="button"
                    className="btn btn-secondary me-2"
                    onClick={() => setShowFormModal(false)}
                  >
                    Cancel
                  </button>
                  <button type="submit" className={`btn ${editingId ? 'btn-warning' : 'btn-primary'}`}>
                    {editingId ? 'Update Course' : 'Add Course'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>

      {/* Delete Confirmation Modal */}
      <div
        className={`modal fade ${showDeleteModal ? 'show d-block' : ''}`}
        tabIndex="-1"
        style={{ backgroundColor: showDeleteModal ? 'rgba(0,0,0,0.5)' : 'transparent' }}
        aria-modal={showDeleteModal ? "true" : "false"}
        role="dialog"
      >
        <div className="modal-dialog modal-dialog-centered">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title text-danger">Confirm Delete</h5>
              <button
                type="button"
                className="btn-close"
                onClick={() => setShowDeleteModal(false)}
              ></button>
            </div>
            <div className="modal-body">
              Are you sure you want to delete this course?
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => setShowDeleteModal(false)}
              >
                Cancel
              </button>
              <button
                type="button"
                className="btn btn-danger"
                onClick={confirmDelete}
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Message Modal */}
      <div
        className={`modal fade ${showMessageModal ? 'show d-block' : ''}`}
        tabIndex="-1"
        style={{ backgroundColor: showMessageModal ? 'rgba(0,0,0,0.5)' : 'transparent' }}
        aria-modal={showMessageModal ? "true" : "false"}
        role="dialog"
      >
        <div className="modal-dialog modal-dialog-centered">
          <div className="modal-content">
            <div className="modal-body text-center">
              <p>{message}</p>
              <button
                type="button"
                className="btn btn-primary"
                onClick={() => setShowMessageModal(false)}
              >
                OK
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
