import React, { useState } from 'react';
import App from './App'; // Course Management
import StudentRegistration from './StudentRegistration';
import MarkManagement from './MarkManagement'; // Import MarkManagement

function Main() {
  const [page, setPage] = useState('home');

  return (
    <div>
      <nav className="navbar navbar-dark bg-dark px-4 shadow-sm">
        <div className="container-fluid">
          {/* Right aligned buttons */}
          <div className="ms-auto">
            <div className="btn-group">
                <button
                    className={`btn btn-outline-light me-2 ${page === 'home' ? 'active' : ''}`}
                    onClick={() => setPage('home')}
                >
                    Course Management
                </button>
                <button
                    className={`btn btn-outline-light me-2 ${page === 'student' ? 'active' : ''}`}
                    onClick={() => setPage('student')}
                >
                    Student Registration
                </button>
                <button
                    className={`btn btn-outline-light ${page === 'marks' ? 'active' : ''}`}
                    onClick={() => setPage('marks')}
                >
                    Mark Management
                </button>
                </div>

          </div>
        </div>
      </nav>

      {page === 'home' && <App />}
      {page === 'student' && <StudentRegistration />}
      {page === 'marks' && <MarkManagement />}
    </div>
  );
}

export default Main;
