import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal, Button } from 'react-bootstrap';
import './App.css';

function App() {
  const [courses, setCourses] = useState([]);
  const [title, setTitle] = useState('');
  const [code, setCode] = useState('');
  const [description, setDescription] = useState('');
  const [credits, setCredits] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  const fetchCourses = () => {
    fetch("http://localhost:8080/api/courses")
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) setCourses(data);
        else setCourses([]);
      });
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  const handleSubmit = () => {
    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8080/api/courses/${editingId}`
      : `http://localhost:8080/api/courses`;

    fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ code, title, description, credits }),
    }).then(() => {
      fetchCourses();
      setCode('');
      setTitle('');
      setDescription('');
      setCredits('');
      setEditingId(null);
      showFeedbackModal(editingId ? 'Course updated successfully!' : 'Course added successfully!');
    });
  };

  const handleEdit = (course) => {
    setEditingId(course.id);
    setCode(course.code);
    setTitle(course.title);
    setDescription(course.description);
    setCredits(course.credits);
  };

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this course?")) {
      fetch(`http://localhost:8080/api/courses/${id}`, { method: "DELETE" })
        .then(() => {
          fetchCourses();
          showFeedbackModal("Course deleted successfully!");
        });
    }
  };

  const showFeedbackModal = (message) => {
    setModalMessage(message);
    setShowModal(true);
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-center">ðŸ“˜ University Course Management</h2>

      <div className="card p-4 mb-5 shadow-sm">
        <h4 className="mb-3">{editingId ? 'Update Course' : 'Add New Course'}</h4>
        <div className="row g-3">
          <div className="col-md-6">
            <input
              type="text"
              className="form-control"
              placeholder="Course Code"
              value={code}
              onChange={e => setCode(e.target.value)}
            />
          </div>
          <div className="col-md-6">
            <input
              type="text"
              className="form-control"
              placeholder="Course Title"
              value={title}
              onChange={e => setTitle(e.target.value)}
            />
          </div>
          <div className="col-md-12">
            <textarea
              className="form-control"
              placeholder="Course Description"
              rows="3"
              value={description}
              onChange={e => setDescription(e.target.value)}
            />
          </div>
          <div className="col-md-6">
            <input
              type="number"
              className="form-control"
              placeholder="Credits"
              value={credits}
              onChange={e => setCredits(e.target.value)}
            />
          </div>
          <div className="col-md-6 text-end">
            <button className="btn btn-primary" onClick={handleSubmit}>
              {editingId ? 'Update Course' : 'Add Course'}
            </button>
          </div>
        </div>
      </div>

      <div className="table-responsive">
        <table className="table table-bordered table-hover shadow-sm">
          <thead className="table-dark">
            <tr>
              <th>Code</th>
              <th>Title</th>
              <th>Description</th>
              <th>Credits</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {courses.map(course => (
              <tr key={course.id}>
                <td>{course.code}</td>
                <td>{course.title}</td>
                <td>{course.description}</td>
                <td>{course.credits}</td>
                <td>
                  <button
                    className="btn btn-sm btn-warning me-2"
                    onClick={() => handleEdit(course)}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-sm btn-danger"
                    onClick={() => handleDelete(course.id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
            {courses.length === 0 && (
              <tr>
                <td colSpan="5" className="text-center text-muted">No courses found.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Feedback Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>Notification</Modal.Title>
        </Modal.Header>
        <Modal.Body>{modalMessage}</Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={() => setShowModal(false)}>OK</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default App;
