import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [courses, setCourses] = useState([]);
  const [title, setTitle] = useState('');
  const [code, setCode] = useState('');
  const [description, setDescription] = useState('');
  const [credits, setCredits] = useState('');
  const [editingId, setEditingId] = useState(null);

  const fetchCourses = () => {
    fetch("http://localhost:8080/api/courses")
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) setCourses(data);
        else setCourses([]);
      });
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  const handleSubmit = () => {
    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8080/api/courses/${editingId}`
      : `http://localhost:8080/api/courses`;

    fetch(url, {
      method: method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ code, title, description, credits }),
    }).then(() => {
      fetchCourses();
      setCode('');
      setTitle('');
      setDescription('');
      setCredits('');
      setEditingId(null);
    });
  };

  const handleEdit = (course) => {
    setEditingId(course.id);
    setCode(course.code);
    setTitle(course.title);
    setDescription(course.description);
    setCredits(course.credits);
  };

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this course?")) {
      fetch(`http://localhost:8080/api/courses/${id}`, { method: "DELETE" })
        .then(() => fetchCourses());
    }
  };

  return (
    <div className="container my-5" style={{ maxWidth: '900px', fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" }}>
      <h2 className="text-center mb-4 fw-bold text-primary" style={{ letterSpacing: '1.2px', textShadow: '0 1px 2px rgba(0,0,0,0.1)' }}>
        University Course Management
      </h2>

      {/* Form Section */}
      <div className="card shadow border-0 rounded-4 mb-5 p-4" style={{ background: 'linear-gradient(135deg, #f9fafb 0%, #e3f2fd 100%)' }}>
        <div className="row g-3 align-items-center">
          <div className="col-md-3">
            <input
              type="text"
              className="form-control rounded-pill border-0 shadow-sm"
              placeholder="Course Code"
              value={code}
              onChange={e => setCode(e.target.value)}
            />
          </div>
          <div className="col-md-3">
            <input
              type="text"
              className="form-control rounded-pill border-0 shadow-sm"
              placeholder="Course Title"
              value={title}
              onChange={e => setTitle(e.target.value)}
            />
          </div>
          <div className="col-md-4">
            <textarea
              className="form-control rounded-pill border-0 shadow-sm"
              placeholder="Course Description"
              value={description}
              onChange={e => setDescription(e.target.value)}
              rows={1}
            />
          </div>
          <div className="col-md-1">
            <input
              type="number"
              className="form-control rounded-pill border-0 shadow-sm text-center"
              placeholder="Credits"
              value={credits}
              onChange={e => setCredits(e.target.value)}
              min="0"
            />
          </div>
          <div className="col-md-1 d-grid">
            <button className="btn btn-primary rounded-pill shadow-lg fw-semibold" onClick={handleSubmit}>
              {editingId ? 'Update' : 'Add'}
            </button>
          </div>
        </div>
      </div>

      {/* Table Section */}
      <div className="table-responsive shadow rounded-4 overflow-hidden" style={{ boxShadow: '0 8px 24px rgba(0,123,255,.15)' }}>
        <table className="table table-hover align-middle mb-0" style={{ backgroundColor: '#ffffff' }}>
          <thead className="bg-primary text-white text-center" style={{ letterSpacing: '0.1em' }}>
            <tr>
              <th className="py-3">Code</th>
              <th className="py-3">Title</th>
              <th className="py-3">Description</th>
              <th className="py-3" style={{ width: '80px' }}>Credits</th>
              <th className="py-3" style={{ minWidth: '160px' }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {courses.map(course => (
              <tr key={course.id} className="text-secondary" style={{ fontSize: '0.95rem' }}>
                <td className="fw-semibold text-dark">{course.code}</td>
                <td>{course.title}</td>
                <td className="text-truncate" style={{ maxWidth: '350px' }}>{course.description}</td>
                <td className="text-center fw-semibold">{course.credits}</td>
                <td className="text-center">
                  <button
                    className="btn btn-sm btn-outline-warning me-2 shadow-sm fw-semibold"
                    onClick={() => handleEdit(course)}
                    style={{ minWidth: '70px' }}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-sm btn-outline-danger shadow-sm fw-semibold"
                    onClick={() => handleDelete(course.id)}
                    style={{ minWidth: '70px' }}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
            {courses.length === 0 && (
              <tr>
                <td colSpan="5" className="text-center text-muted py-4 fst-italic">
                  No courses available. Please add some courses.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
