import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from "react-router-dom";
import App from "./App"; // Course Management
import StudentRegistration from "./StudentRegistration";
import MarkManagement from "./MarkManagement";
import Login from "./Login";
import Register from "./Register";
import "./Main.css";

// âœ… Protected Route wrapper
const PrivateRoute = ({ children }) => {
  const user = JSON.parse(localStorage.getItem("user"));
  return user ? children : <Navigate to="/login" />;
};

// âœ… Navbar for authenticated users only
const Navbar = () => {
  const handleLogout = () => {
    localStorage.removeItem("user");
    window.location.href = "/login";
  };

  return (
    <nav
      className="navbar px-4 shadow-sm"
      style={{
        background: "linear-gradient(90deg, #0d6efd, #6610f2)",
        paddingTop: "0.75rem",
        paddingBottom: "0.75rem",
      }}
    >
      <div className="container-fluid d-flex align-items-center justify-content-between">
        <span className="navbar-brand text-white fw-bold fs-4">ðŸŽ“ Uni Dashboard</span>

        <div className="btn-group">
          <Link to="/dashboard" className="btn nav-btn me-2 btn-outline-light text-white">
            Course Management
          </Link>
          <Link to="/dashboard/students" className="btn nav-btn me-2 btn-outline-light text-white">
            Student Registration
          </Link>
          <Link to="/dashboard/marks" className="btn nav-btn btn-outline-light text-white">
            Marks Management
          </Link>
          <button className="btn btn-outline-light text-white ms-2" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>
    </nav>
  );
};

function Main() {
  return (
    <Router>
      <Routes>
        {/* Public routes */}
        <Route path="/" element={<Navigate to="/login" />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* Protected dashboard routes */}
        <Route
          path="/dashboard/*"
          element={
            <PrivateRoute>
              <Navbar />
              <Routes>
                <Route index element={<App />} />
                <Route path="students" element={<StudentRegistration />} />
                <Route path="marks" element={<MarkManagement />} />
              </Routes>
            </PrivateRoute>
          }
        />

        {/* Catch-all: redirect unknown routes to login */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default Main;
