import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [courses, setCourses] = useState([]);
  const [title, setTitle] = useState('');
  const [code, setCode] = useState('');
  const [description, setDescription] = useState('');
  const [credits, setCredits] = useState('');
  const [editingId, setEditingId] = useState(null);

  // Fetch all courses
  const fetchCourses = () => {
    fetch("http://localhost:8080/api/courses")
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setCourses(data);
        } else {
          setCourses([]); // Safety fallback
        }
      });
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  // Add or update
  const handleSubmit = () => {
    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8080/api/courses/${editingId}`
      : `http://localhost:8080/api/courses`;

    fetch(url, {
      method: method,
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ code, title, description, credits })
    })
      .then(() => {
        fetchCourses();
        setCode('');
        setTitle('');
        setDescription('');
        setCredits('');
        setEditingId(null);
      });
  };

  // Start editing
  const handleEdit = (course) => {
    setEditingId(course.id);
    setCode(course.code);
    setTitle(course.title);
    setDescription(course.description);
    setCredits(course.credits);
  };

  // Delete course
  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this course?")) {
      fetch(`http://localhost:8080/api/courses/${id}`, {
        method: "DELETE"
      }).then(() => fetchCourses());
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-center">University Course Management</h2>

      {/* Course Form Section */}
      <div className="card mb-4 shadow-sm">
        <div className="card-body">
          <h5 className="card-title">{editingId ? 'Edit Course' : 'Add New Course'}</h5>
          <form onSubmit={e => { e.preventDefault(); handleSubmit(); }}>
            <div className="row g-3">
              <div className="col-md-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Course Code"
                  value={code}
                  onChange={e => setCode(e.target.value)}
                  required
                />
              </div>
              <div className="col-md-5">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Course Title"
                  value={title}
                  onChange={e => setTitle(e.target.value)}
                  required
                />
              </div>
              <div className="col-md-4">
                <input
                  type="number"
                  className="form-control"
                  placeholder="Credits"
                  value={credits}
                  onChange={e => setCredits(e.target.value)}
                  min="0"
                  required
                />
              </div>
            </div>

            <div className="mt-3">
              <textarea
                className="form-control"
                placeholder="Course Description"
                rows="3"
                value={description}
                onChange={e => setDescription(e.target.value)}
                required
              />
            </div>

            <div className="mt-3 text-center">
              <button type="submit" className={`btn ${editingId ? 'btn-warning' : 'btn-primary'}`}>
                {editingId ? 'Update Course' : 'Add Course'}
              </button>
              {editingId && (
                <button
                  type="button"
                  className="btn btn-secondary ms-2"
                  onClick={() => {
                    setEditingId(null);
                    setCode('');
                    setTitle('');
                    setDescription('');
                    setCredits('');
                  }}
                >
                  Cancel
                </button>
              )}
            </div>
          </form>
        </div>
      </div>

      {/* Table Section */}
      <div className="card shadow-sm">
        <div className="card-body">
          <h4 className="card-title mb-3">📋 Course List</h4>
          <div className="table-responsive">
            <table className="table table-striped table-hover align-middle">
              <thead className="table-dark">
                <tr>
                  <th>Code</th>
                  <th>Title</th>
                  <th>Description</th>
                  <th>Credits</th>
                  <th className="text-center">Actions</th>
                </tr>
              </thead>
              <tbody>
                {courses.length === 0 ? (
                  <tr>
                    <td colSpan="5" className="text-center text-muted">No courses found.</td>
                  </tr>
                ) : (
                  courses.map(course => (
                    <tr key={course.id}>
                      <td>{course.code}</td>
                      <td>{course.title}</td>
                      <td>{course.description}</td>
                      <td>{course.credits}</td>
                      <td className="text-center">
                        <button
                          className="btn btn-sm btn-outline-primary me-2"
                          onClick={() => handleEdit(course)}
                        >
                          Edit
                        </button>
                        <button
                          className="btn btn-sm btn-outline-danger"
                          onClick={() => handleDelete(course.id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
