import React, { useState } from "react";
const API_URL = process.env.REACT_APP_API_URL;

function ResetPassword() {
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleReset = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await fetch(`${API_URL}/api/auth/reset-password`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Reset failed");
      setMessage("Check your email for reset instructions!");
    } catch (err) {
      setMessage(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100" style={{ background: 'linear-gradient(135deg,rgb(77, 158, 202), #0d6efd)' }}>
      <div className="card shadow-lg p-4 rounded-4" style={{ width: '400px', backgroundColor: '#ffffff' }}>
        <h2 className="text-center mb-4 fw-bold text-primary">ðŸ”‘ Reset Password</h2>
        <form onSubmit={handleReset}>
          <div className="mb-3">
            <input
              type="email"
              className="form-control form-control-lg"
              placeholder="Enter your email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
              disabled={loading}
            />
          </div>
          <button type="submit" className="btn btn-primary w-100 btn-lg fw-bold" disabled={loading}>
            {loading ? "Processing..." : "Reset Password"}
          </button>
        </form>
        {message && <p className="mt-3 text-center text-success">{message}</p>}
        <div className="text-center mt-2">
          <a href="/login" className="text-primary text-decoration-none">Back to Login</a>
        </div>
      </div>
    </div>
  );
}

export default ResetPassword;
