import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

function App() {
  const [courses, setCourses] = useState([]);
  const [title, setTitle] = useState('');
  const [code, setCode] = useState('');
  const [description, setDescription] = useState('');
  const [credits, setCredits] = useState('');
  const [editingId, setEditingId] = useState(null);

  const fetchCourses = () => {
    fetch("http://localhost:8080/api/courses")
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setCourses(data);
        } else {
          setCourses([]);
        }
      });
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  const handleSubmit = () => {
    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8080/api/courses/${editingId}`
      : `http://localhost:8080/api/courses`;

    fetch(url, {
      method: method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ code, title, description, credits }),
    }).then(() => {
      fetchCourses();
      setCode('');
      setTitle('');
      setDescription('');
      setCredits('');
      setEditingId(null);
    });
  };

  const handleEdit = (course) => {
    setEditingId(course.id);
    setCode(course.code);
    setTitle(course.title);
    setDescription(course.description);
    setCredits(course.credits);
  };

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this course?")) {
      fetch(`http://localhost:8080/api/courses/${id}`, { method: "DELETE" })
        .then(() => fetchCourses());
    }
  };

  return (
    <div className="container my-5">
      <h2 className="text-center mb-4">University Course Management</h2>

      {/* Form Section */}
      <div className="card p-4 mb-5 shadow-sm">
        <div className="row g-3">
          <div className="col-md-3">
            <input
              type="text"
              className="form-control"
              placeholder="Course Code"
              value={code}
              onChange={e => setCode(e.target.value)}
            />
          </div>
          <div className="col-md-3">
            <input
              type="text"
              className="form-control"
              placeholder="Course Title"
              value={title}
              onChange={e => setTitle(e.target.value)}
            />
          </div>
          <div className="col-md-4">
            <textarea
              className="form-control"
              placeholder="Course Description"
              value={description}
              onChange={e => setDescription(e.target.value)}
              rows={1}
            />
          </div>
          <div className="col-md-1">
            <input
              type="number"
              className="form-control"
              placeholder="Credits"
              value={credits}
              onChange={e => setCredits(e.target.value)}
            />
          </div>
          <div className="col-md-1 d-grid">
            <button className="btn btn-primary" onClick={handleSubmit}>
              {editingId ? 'Update' : 'Add'}
            </button>
          </div>
        </div>
      </div>

      {/* Table Section */}
      <div className="table-responsive shadow-sm rounded">
        <table className="table table-striped table-bordered align-middle">
          <thead className="table-primary text-center">
            <tr>
              <th>Code</th>
              <th>Title</th>
              <th>Description</th>
              <th>Credits</th>
              <th style={{ minWidth: '140px' }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {courses.map(course => (
              <tr key={course.id}>
                <td>{course.code}</td>
                <td>{course.title}</td>
                <td>{course.description}</td>
                <td className="text-center">{course.credits}</td>
                <td className="text-center">
                  <button
                    className="btn btn-sm btn-warning me-2"
                    onClick={() => handleEdit(course)}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-sm btn-danger"
                    onClick={() => handleDelete(course.id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
