import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
const API_URL = process.env.REACT_APP_API_URL;

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [showMessageModal, setShowMessageModal] = useState(false);

  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    setLoading(true);

    fetch(`${API_URL}/api/auth/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
    })
      .then(async res => {
        if (!res.ok) {
          const errorData = await res.json().catch(() => ({}));
          throw new Error(errorData.message || 'Login failed');
        }
        return res.json();
      })
      .then(data => {
        localStorage.setItem('user', JSON.stringify(data));
        navigate('/dashboard');
      })
      .catch(err => {
        setMessage(err.message);
        setShowMessageModal(true);
        setLoading(false);
      });
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100" style={{ background: 'linear-gradient(135deg,rgb(77, 158, 202), #0d6efd)' }}>
      <div className="card shadow-lg p-4 rounded-4" style={{ width: '400px', backgroundColor: '#ffffff' }}>
        <h2 className="text-center mb-4 fw-bold text-primary">🔑 Login</h2>
        <form onSubmit={handleLogin}>
          <div className="mb-3">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Username"
              value={username}
              onChange={e => setUsername(e.target.value)}
              required
              disabled={loading}
            />
          </div>
          <div className="mb-3">
            <input
              type="password"
              className="form-control form-control-lg"
              placeholder="Password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
              disabled={loading}
            />
          </div>

          <div className="text-end mb-3">
            <Link to="/reset-password" className="text-decoration-none text-primary fw-bold">
              Forgot Password?
            </Link>
          </div>

          <button type="submit" className="btn btn-primary w-100 btn-lg fw-bold" disabled={loading}>
            {loading ? 'Processing...' : 'Login'}
          </button>
        </form>

        <div className="text-center mt-3">
          <p className="mb-0">
            Don’t have an account?{' '}
            <Link to="/register" className="text-primary fw-bold text-decoration-none">
              Register
            </Link>
          </p>
        </div>

        {/* Message Modal */}
        {showMessageModal && (
          <div className="modal fade show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
            <div className="modal-dialog modal-dialog-centered">
              <div className="modal-content border-primary rounded-4">
                <div className="modal-body text-center">
                  <p>{message}</p>
                  <button className="btn btn-primary" onClick={() => setShowMessageModal(false)}>OK</button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Login;
