import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'; // your custom styles

function App() {
  const [courses, setCourses] = useState([]);
  const [title, setTitle] = useState('');
  const [code, setCode] = useState('');
  const [description, setDescription] = useState('');
  const [credits, setCredits] = useState('');
  const [editingId, setEditingId] = useState(null);

  const fetchCourses = () => {
    fetch("http://localhost:8080/api/courses")
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) setCourses(data);
        else setCourses([]);
      });
  };

  useEffect(() => fetchCourses(), []);

  const handleSubmit = () => {
    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8080/api/courses/${editingId}`
      : `http://localhost:8080/api/courses`;

    fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ code, title, description, credits }),
    }).then(() => {
      fetchCourses();
      setCode('');
      setTitle('');
      setDescription('');
      setCredits('');
      setEditingId(null);
    });
  };

  const handleEdit = (course) => {
    setEditingId(course.id);
    setCode(course.code);
    setTitle(course.title);
    setDescription(course.description);
    setCredits(course.credits);
  };

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this course?")) {
      fetch(`http://localhost:8080/api/courses/${id}`, { method: "DELETE" })
        .then(() => fetchCourses());
    }
  };

  return (
    <div className="container my-5" style={{ maxWidth: '900px', fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" }}>
      <h2 className="text-center mb-5 fw-bold text-primary">University Course Management</h2>

      {/* Form Section */}
      <form className="row gx-3 gy-3 align-items-center mb-5">
        <div className="col-md-3">
          <input
            type="text"
            className="form-control form-control-lg"
            placeholder="Course Code"
            value={code}
            onChange={e => setCode(e.target.value)}
          />
        </div>
        <div className="col-md-3">
          <input
            type="text"
            className="form-control form-control-lg"
            placeholder="Course Title"
            value={title}
            onChange={e => setTitle(e.target.value)}
          />
        </div>
        <div className="col-md-4">
          <textarea
            className="form-control form-control-lg"
            placeholder="Course Description"
            value={description}
            onChange={e => setDescription(e.target.value)}
            rows={1}
          />
        </div>
        <div className="col-md-1">
          <input
            type="number"
            className="form-control form-control-lg text-center"
            placeholder="Credits"
            value={credits}
            onChange={e => setCredits(e.target.value)}
            min="0"
          />
        </div>
        <div className="col-md-1 d-grid">
          <button
            type="button"
            className="btn btn-primary btn-lg fw-semibold"
            onClick={handleSubmit}
          >
            {editingId ? 'Update' : 'Add'}
          </button>
        </div>
      </form>

      {/* Table Section */}
      <div className="table-responsive rounded" style={{ border: '1px solid #ddd' }}>
        <table className="table table-hover align-middle mb-0">
          <thead className="table-light text-center">
            <tr>
              <th style={{ width: '12%' }}>Code</th>
              <th style={{ width: '22%' }}>Title</th>
              <th style={{ width: '40%' }}>Description</th>
              <th style={{ width: '8%' }}>Credits</th>
              <th style={{ width: '18%' }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {courses.length === 0 ? (
              <tr>
                <td colSpan="5" className="text-center py-4 fst-italic text-muted">
                  No courses available. Please add some.
                </td>
              </tr>
            ) : (
              courses.map(course => (
                <tr key={course.id}>
                  <td className="fw-semibold text-primary">{course.code}</td>
                  <td>{course.title}</td>
                  <td className="text-truncate" style={{ maxWidth: '400px' }}>{course.description}</td>
                  <td className="text-center fw-semibold">{course.credits}</td>
                  <td className="text-center">
                    <button
                      className="btn btn-outline-warning btn-sm me-2 fw-semibold"
                      onClick={() => handleEdit(course)}
                      style={{ minWidth: '75px' }}
                    >
                      Edit
                    </button>
                    <button
                      className="btn btn-outline-danger btn-sm fw-semibold"
                      onClick={() => handleDelete(course.id)}
                      style={{ minWidth: '75px' }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
