import React, { useState, useEffect } from 'react';

function StudentRegistration() {
  const [students, setStudents] = useState([]);
  const [studentId, setStudentId] = useState(''); 
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [course, setCourse] = useState('');

  const fetchStudents = () => {
    fetch('http://localhost:8080/api/students')
      .then(res => res.json())
      .then(data => setStudents(data))
      .catch(err => console.error("Failed to fetch students:", err));
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Include student_id in payload as 'student_id'
    fetch('http://localhost:8080/api/students', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({studentId: studentId, name, email, course })
    })
      .then(res => {
        if (!res.ok) throw new Error('Failed to register student');
        return res.json();
      })
      .then(() => {
        setStudentId('');
        setName('');
        setEmail('');
        setCourse('');
        fetchStudents();
      })
      .catch(err => alert("Error: " + err.message));
  };

  const deleteStudent = (id) => {
    fetch(`http://localhost:8080/api/students/${id}`, {
      method: 'DELETE'
    })
      .then(() => fetchStudents())
      .catch(err => alert("Failed to delete student"));
  };

  return (
    <div className="container mt-5">
      <h3 className="text-center text-success mb-4">👨‍🎓 Student Registration</h3>

      <form className="row g-3 mb-4" onSubmit={handleSubmit}>
        <div className="col-md-3">
          <input
            type="text"
            className="form-control"
            placeholder="Student ID"
            value={studentId}
            onChange={e => setStudentId(e.target.value)}
            required
          />
        </div>
        <div className="col-md-3">
          <input
            type="text"
            className="form-control"
            placeholder="Full Name"
            value={name}
            onChange={e => setName(e.target.value)}
            required
          />
        </div>
        <div className="col-md-3">
          <input
            type="email"
            className="form-control"
            placeholder="Email"
            value={email}
            onChange={e => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="col-md-3">
          <input
            type="text"
            className="form-control"
            placeholder="Course"
            value={course}
            onChange={e => setCourse(e.target.value)}
          />
        </div>

        <div className="col-12 text-end">
          <button type="submit" className="btn btn-primary">Register Student</button>
        </div>
      </form>

      <table className="table table-bordered table-hover">
        <thead className="table-secondary text-center">
          <tr>
            <th>Student ID</th> {/* Manual student_id */}
            <th>Name</th>
            <th>Email</th>
            <th>Course</th>
            <th>Registration Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.length === 0 ? (
            <tr><td colSpan="7" className="text-center text-muted">No students found.</td></tr>
          ) : (
            students.map(student => (
              <tr key={student.id}>
                <td className="text-center">{student.studentId}</td>
                <td>{student.name}</td>
                <td>{student.email}</td>
                <td>{student.course}</td>
                <td className="text-center">{student.registration_date || student.registrationDate}</td>
                <td className="text-center">
                  <button className="btn btn-sm btn-danger" onClick={() => deleteStudent(student.id)}>Delete</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default StudentRegistration;
