import React, { useState, useEffect } from 'react';

function StudentRegistration() {
  const [students, setStudents] = useState([]);
  const [studentId, setStudentId] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [course, setCourse] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchStudents = () => {
    setLoading(true);
    fetch('http://localhost:8080/api/students')
      .then(res => {
        if (!res.ok) throw new Error('Failed to fetch students');
        return res.json();
      })
      .then(data => {
        setStudents(data);
        setLoading(false);
        setError(null);
      })
      .catch(err => {
        console.error(err);
        setError(err.message);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    fetch('http://localhost:8080/api/students', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ studentId, name, email, course }),
    })
      .then(res => {
        if (!res.ok) throw new Error('Failed to register student');
        return res.json();
      })
      .then(() => {
        setStudentId('');
        setName('');
        setEmail('');
        setCourse('');
        fetchStudents();
      })
      .catch(err => {
        alert(err.message);
        setLoading(false);
      });
  };

  const deleteStudent = (id) => {
    if (!window.confirm('Are you sure you want to delete this student?')) return;

    fetch(`http://localhost:8080/api/students/${id}`, { method: 'DELETE' })
      .then(res => {
        if (!res.ok) throw new Error('Failed to delete student');
        fetchStudents();
      })
      .catch(err => alert(err.message));
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-center text-primary">Student Registration</h2>

      {/* Form */}
      <form className="row g-3 mb-4" onSubmit={handleSubmit}>
        <div className="col-md-3">
          <label htmlFor="studentId" className="form-label">Student ID <span className="text-danger">*</span></label>
          <input
            id="studentId"
            type="text"
            className="form-control"
            placeholder="Enter Student ID"
            value={studentId}
            onChange={e => setStudentId(e.target.value)}
            required
            disabled={loading}
          />
        </div>

        <div className="col-md-3">
          <label htmlFor="name" className="form-label">Full Name <span className="text-danger">*</span></label>
          <input
            id="name"
            type="text"
            className="form-control"
            placeholder="Enter Full Name"
            value={name}
            onChange={e => setName(e.target.value)}
            required
            disabled={loading}
          />
        </div>

        <div className="col-md-3">
          <label htmlFor="email" className="form-label">Email <span className="text-danger">*</span></label>
          <input
            id="email"
            type="email"
            className="form-control"
            placeholder="Enter Email"
            value={email}
            onChange={e => setEmail(e.target.value)}
            required
            disabled={loading}
          />
        </div>

        <div className="col-md-3">
          <label htmlFor="course" className="form-label">Course</label>
          <input
            id="course"
            type="text"
            className="form-control"
            placeholder="Enter Course"
            value={course}
            onChange={e => setCourse(e.target.value)}
            disabled={loading}
          />
        </div>

        <div className="col-12 text-end">
          <button type="submit" className="btn btn-success" disabled={loading}>
            {loading ? 'Processing...' : 'Register Student'}
          </button>
        </div>
      </form>

      {/* Error message */}
      {error && <div className="alert alert-danger text-center">{error}</div>}

      {/* Table */}
      <div className="table-responsive">
        <table className="table table-striped table-bordered align-middle">
          <thead className="table-primary text-center">
            <tr>
              <th>Student ID</th>
              <th>Name</th>
              <th>Email</th>
              <th>Course</th>
              <th>Registration Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {loading && students.length === 0 ? (
              <tr><td colSpan="6" className="text-center">Loading students...</td></tr>
            ) : students.length === 0 ? (
              <tr><td colSpan="6" className="text-center text-muted">No students found.</td></tr>
            ) : (
              students.map(student => (
                <tr key={student.id}>
                  <td className="text-center">{student.studentId}</td>
                  <td>{student.name}</td>
                  <td>{student.email}</td>
                  <td>{student.course}</td>
                  <td className="text-center">{student.registration_date || student.registrationDate}</td>
                  <td className="text-center">
                    <button
                      className="btn btn-sm btn-danger"
                      onClick={() => deleteStudent(student.id)}
                      disabled={loading}
                      title="Delete Student"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default StudentRegistration;
