import React, { useEffect, useState } from 'react';
import './App.css'; // Custom styling

function App() {
  const [courses, setCourses] = useState([]);
  const [title, setTitle] = useState('');
  const [code, setCode] = useState('');
  const [description, setDescription] = useState('');
  const [credits, setCredits] = useState('');
  const [editingId, setEditingId] = useState(null);

  // Fetch all courses
  const fetchCourses = () => {
    fetch("http://localhost:8080/api/courses")
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setCourses(data);
        } else {
          setCourses([]); // Safety fallback
        }
      });
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  // Add or update
  const handleSubmit = () => {
    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8080/api/courses/${editingId}`
      : `http://localhost:8080/api/courses`;

    fetch(url, {
      method: method,
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ code, title, description, credits })
    })
      .then(() => {
        fetchCourses();
        setCode('');
        setTitle('');
        setDescription('');
        setCredits('');
        setEditingId(null);
      });
  };

  // Start editing
  const handleEdit = (course) => {
    setEditingId(course.id);
    setCode(course.code);
    setTitle(course.title);
    setDescription(course.description);
    setCredits(course.credits);
  };

  // Delete course
  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this course?")) {
      fetch(`http://localhost:8080/api/courses/${id}`, {
        method: "DELETE"
      }).then(() => fetchCourses());
    }
  };

  return (
  <div className="container">
    <h2>University Course Management</h2>

    {/* Course Form Section */}
    <div className="form-section">
      <div className="form-group">
        <input
          type="text"
          placeholder="Course Code"
          value={code}
          onChange={e => setCode(e.target.value)}
        />
        <input
          type="text"
          placeholder="Course Title"
          value={title}
          onChange={e => setTitle(e.target.value)}
        />
        <textarea
          placeholder="Course Description"
          value={description}
          onChange={e => setDescription(e.target.value)}
        />
        <input
          type="number"
          placeholder="Credits"
          value={credits}
          onChange={e => setCredits(e.target.value)}
        />
        <button onClick={handleSubmit}>
          {editingId ? 'Update Course' : 'Add Course'}
        </button>
      </div>
    </div>

    {/* Table Section */}
    <div className="table-container">
      <h4 className="table-title">ðŸ“‹ Course List</h4>
      <table>
        <thead>
          <tr>
            <th>Code</th>
            <th>Title</th>
            <th>Description</th>
            <th>Credits</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {courses.map(course => (
            <tr key={course.id}>
              <td>{course.code}</td>
              <td>{course.title}</td>
              <td>{course.description}</td>
              <td>{course.credits}</td>
              <td>
                <button onClick={() => handleEdit(course)}>Edit</button>
                <button onClick={() => handleDelete(course.id)} className="delete">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  </div>
);

}

export default App;
