import React, { useState } from 'react';
import App from './App'; // Course Management
import StudentRegistration from './StudentRegistration';
import MarkManagement from './MarkManagement'; // Import MarkManagement

function Main() {
  const [page, setPage] = useState('home');

  return (
    <div>
      <nav
        className="navbar px-4 shadow-sm"
        style={{
          background: 'linear-gradient(90deg, #0d6efd, #6610f2)',
          paddingTop: '0.75rem',
          paddingBottom: '0.75rem',
        }}
      >
        <div className="container-fluid d-flex align-items-center justify-content-between">
          <span className="navbar-brand text-white fw-bold fs-4">
            ðŸŽ“ Uni Dashboard
          </span>

          <div className="btn-group">
            <button
              className={`btn text-white ${
                page === 'home' ? 'btn-light text-dark' : 'btn-outline-light'
              } rounded-pill me-2`}
              onClick={() => setPage('home')}
            >
              Course Management
            </button>
            <button
              className={`btn text-white ${
                page === 'student' ? 'btn-light text-dark' : 'btn-outline-light'
              } rounded-pill me-2`}
              onClick={() => setPage('student')}
            >
              Student Registration
            </button>
            <button
              className={`btn text-white ${
                page === 'marks' ? 'btn-light text-dark' : 'btn-outline-light'
              } rounded-pill`}
              onClick={() => setPage('marks')}
            >
              Marks Management
            </button>
          </div>
        </div>
      </nav>

      {/* Conditional Rendering for Components */}
      {page === 'home' && <App />}
      {page === 'student' && <StudentRegistration />}
      {page === 'marks' && <MarkManagement />}
    </div>
  );
}

export default Main;
