import React, { useEffect, useState } from 'react';

function App() {
  const [courses, setCourses] = useState([]);
  const [title, setTitle] = useState('');
  const [code, setCode] = useState('');

  useEffect(() => {
    fetch("http://localhost:8080/api/courses")
      .then(res => res.json())
      .then(data => setCourses(data));
  }, []);

  const handleAdd = () => {
    fetch("http://localhost:8080/api/courses", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ code, title })
    }).then(res => res.json())
      .then(newCourse => setCourses([...courses, newCourse]));

    setCode('');
    setTitle('');
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>University Course Management</h2>

      <input placeholder="Course Code" value={code} onChange={e => setCode(e.target.value)} />
      <input placeholder="Course Title" value={title} onChange={e => setTitle(e.target.value)} />
      <button onClick={handleAdd}>Add Course</button>

      <table border="1" style={{ marginTop: "20px", width: "100%" }}>
        <thead>
          <tr><th>Code</th><th>Title</th></tr>
        </thead>
        <tbody>
          {courses.map(course => (
            <tr key={course.id}><td>{course.code}</td><td>{course.title}</td></tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
