import React, { useState, useEffect } from 'react';

function MarkManagement() {
  const [marks, setMarks] = useState([]);
  const [students, setStudents] = useState([]);
  const [courses, setCourses] = useState([]);

  const [studentId, setStudentId] = useState('');
  const [courseCode, setCourseCode] = useState('');
  const [score, setScore] = useState('');
  const [editingId, setEditingId] = useState(null);

  const fetchMarks = () => {
    fetch('http://localhost:8080/api/marks')
      .then(res => res.json())
      .then(data => setMarks(data))
      .catch(() => alert("Failed to fetch marks"));
  };

  const fetchStudents = () => {
    fetch('http://localhost:8080/api/students')
      .then(res => res.json())
      .then(data => setStudents(data))
      .catch(() => alert("Failed to fetch students"));
  };

  const fetchCourses = () => {
    fetch('http://localhost:8080/api/courses')
      .then(res => res.json())
      .then(data => setCourses(data))
      .catch(() => alert("Failed to fetch courses"));
  };

  useEffect(() => {
    fetchMarks();
    fetchStudents();
    fetchCourses();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8080/api/marks/${editingId}`
      : 'http://localhost:8080/api/marks';

    fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ studentId, courseCode, marks: parseFloat(score) })
    })
      .then(res => res.json())
      .then(() => {
        fetchMarks();
        setStudentId('');
        setCourseCode('');
        setScore('');
        setEditingId(null);
      })
      .catch(() => alert("Error saving mark"));
  };

  const handleEdit = (mark) => {
    setEditingId(mark.id);
    setStudentId(mark.studentId);
    setCourseCode(mark.courseCode);
    setScore(mark.marks);
  };

  const handleDelete = (id) => {
    if (!window.confirm("Delete this mark?")) return;
    fetch(`http://localhost:8080/api/marks/${id}`, { method: 'DELETE' })
      .then(() => fetchMarks())
      .catch(() => alert("Failed to delete mark"));
  };

  return (
    <div className="container mt-5">
      <div className="bg-dark text-white p-3 rounded-3 mb-4 text-center">
        <h2 className="m-0">ðŸŽ¯ Mark Management</h2>
      </div>

      <form onSubmit={handleSubmit} className="row g-3 mb-4">
        <div className="col-md-4">
          <select
            className="form-select"
            value={studentId}
            onChange={e => setStudentId(e.target.value)}
            required
          >
            <option value="">Select Student</option>
            {students.map(s => (
              <option key={s.id} value={s.studentId}>
                {s.studentId} - {s.name}
              </option>
            ))}
          </select>
        </div>

        <div className="col-md-4">
          <select
            className="form-select"
            value={courseCode}
            onChange={e => setCourseCode(e.target.value)}
            required
          >
            <option value="">Select Course</option>
            {courses.map(c => (
              <option key={c.id} value={c.code}>
                {c.code} - {c.title}
              </option>
            ))}
          </select>
        </div>

        <div className="col-md-2">
          <input
            type="number"
            className="form-control"
            placeholder="Marks"
            value={score}
            onChange={e => setScore(e.target.value)}
            required
          />
        </div>

        <div className="col-md-2 text-end">
          <button type="submit" className={`btn ${editingId ? 'btn-warning' : 'btn-success'} w-100`}>
            {editingId ? "Update" : "Add"} Mark
          </button>
        </div>
      </form>

      <table className="table table-bordered table-hover shadow-sm">
        <thead className="table-primary text-center">
          <tr>
            <th>ID</th>
            <th>Student ID</th>
            <th>Course Code</th>
            <th>Marks</th>
            <th>GPA</th>
            <th>Grade</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {marks.length === 0 ? (
            <tr><td colSpan="7" className="text-center text-muted">No marks available.</td></tr>
          ) : (
            marks.map(mark => (
              <tr key={mark.id} className="text-center">
                <td>{mark.id}</td>
                <td>{mark.studentId}</td>
                <td>{mark.courseCode}</td>
                <td>{mark.marks}</td>
                <td>{mark.gpa}</td>
                <td>{mark.grade}</td>
                <td>
                  <button className="btn btn-sm btn-primary me-2" onClick={() => handleEdit(mark)}>Edit</button>
                  <button className="btn btn-sm btn-danger" onClick={() => handleDelete(mark.id)}>Delete</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default MarkManagement;
