import React, { useState, useEffect } from 'react';

function MarkManagement() {
  const [marks, setMarks] = useState([]);
  const [students, setStudents] = useState([]);
  const [courses, setCourses] = useState([]);

  const [studentId, setStudentId] = useState('');
  const [courseCode, setCourseCode] = useState('');
  const [score, setScore] = useState('');
  const [editingId, setEditingId] = useState(null);

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [showMessageModal, setShowMessageModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [deleteMarkId, setDeleteMarkId] = useState(null);

  // Fetch marks
  const fetchMarks = () => {
    setLoading(true);
    fetch('http://localhost:8080/api/marks')
      .then(res => res.json())
      .then(data => {
        setMarks(Array.isArray(data) ? data : []);
        setLoading(false);
      })
      .catch(() => {
        alert("Failed to fetch marks");
        setLoading(false);
      });
  };

  // Fetch students
  const fetchStudents = () => {
    fetch('http://localhost:8080/api/students')
      .then(res => res.json())
      .then(data => setStudents(Array.isArray(data) ? data : []))
      .catch(() => alert("Failed to fetch students"));
  };

  // Fetch courses
  const fetchCourses = () => {
    fetch('http://localhost:8080/api/courses')
      .then(res => res.json())
      .then(data => setCourses(Array.isArray(data) ? data : []))
      .catch(() => alert("Failed to fetch courses"));
  };

  useEffect(() => {
    fetchMarks();
    fetchStudents();
    fetchCourses();
  }, []);

  // Submit form to add or update mark
  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:8080/api/marks/${editingId}`
      : 'http://localhost:8080/api/marks';

    fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ studentId, courseCode, marks: parseFloat(score) }),
    })
      .then(res => {
        if (!res.ok) throw new Error('Failed to save mark');
        return res.json();
      })
      .then(() => {
        fetchMarks();
        resetForm();
        setMessage(editingId ? 'Mark updated successfully!' : 'Mark added successfully!');
        setShowMessageModal(true);
        setLoading(false);
      })
      .catch(err => {
        setMessage('Error: ' + err.message);
        setShowMessageModal(true);
        setLoading(false);
      });
  };

  // Reset form inputs
  const resetForm = () => {
    setStudentId('');
    setCourseCode('');
    setScore('');
    setEditingId(null);
  };

  // Prepare form for editing a mark
  const handleEdit = (mark) => {
    setEditingId(mark.id);
    setStudentId(mark.studentId);
    setCourseCode(mark.courseCode);
    setScore(mark.marks);
  };

  // Open delete confirmation modal
  const openDeleteModal = (id) => {
    setDeleteMarkId(id);
    setShowDeleteModal(true);
  };

  // Confirm delete action
  const confirmDelete = () => {
    setLoading(true);
    fetch(`http://localhost:8080/api/marks/${deleteMarkId}`, { method: 'DELETE' })
      .then(res => {
        if (!res.ok) throw new Error('Failed to delete mark');
        fetchMarks();
        setShowDeleteModal(false);
        setMessage('Mark deleted successfully!');
        setShowMessageModal(true);
        setLoading(false);
      })
      .catch(err => {
        setShowDeleteModal(false);
        setMessage('Error deleting mark: ' + err.message);
        setShowMessageModal(true);
        setLoading(false);
      });
  };

  return (
    <div className="container mt-5 p-4 rounded shadow" style={{ backgroundColor: '#f8f9fa', minHeight: '90vh' }}>
      <div className="bg-dark text-white py-3 px-4 rounded-3 shadow mb-4 text-center">
        <h2 className="m-0">🎯 Mark Management</h2>
      </div>

      <form onSubmit={handleSubmit} className="row g-3 mb-4">
        <div className="col-md-4">
          <select
            className="form-select"
            value={studentId}
            onChange={e => setStudentId(e.target.value)}
            required
            disabled={loading}
          >
            <option value="">Select Student</option>
            {students.map(s => (
              <option key={s.id} value={s.studentId}>
                {s.studentId} - {s.name}
              </option>
            ))}
          </select>
        </div>

        <div className="col-md-4">
          <select
            className="form-select"
            value={courseCode}
            onChange={e => setCourseCode(e.target.value)}
            required
            disabled={loading}
          >
            <option value="">Select Course</option>
            {courses.map(c => (
              <option key={c.id} value={c.code}>
                {c.code} - {c.title}
              </option>
            ))}
          </select>
        </div>

        <div className="col-md-2">
          <input
            type="number"
            className="form-control"
            placeholder="Marks"
            value={score}
            onChange={e => setScore(e.target.value)}
            required
            disabled={loading}
          />
        </div>

        <div className="col-md-2 text-end">
          <button type="submit" className={`btn ${editingId ? 'btn-warning' : 'btn-success'} w-100`} disabled={loading}>
            {loading ? 'Processing...' : editingId ? "Update" : "Add"} Mark
          </button>
        </div>
      </form>

      <table className="table table-bordered table-hover shadow-sm">
        <thead className="table-primary text-center">
          <tr>
            <th>ID</th>
            <th>Student ID</th>
            <th>Course Code</th>
            <th>Marks</th>
            <th>GPA</th>
            <th>Grade</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {marks.length === 0 ? (
            <tr><td colSpan="7" className="text-center text-muted">No marks available.</td></tr>
          ) : (
            marks.map(mark => (
              <tr key={mark.id} className="text-center">
                <td>{mark.id}</td>
                <td>{mark.studentId}</td>
                <td>{mark.courseCode}</td>
                <td>{mark.marks}</td>
                <td>{mark.gpa}</td>
                <td>{mark.grade}</td>
                <td>
                  <button
                    className="btn btn-sm btn-primary me-2"
                    onClick={() => handleEdit(mark)}
                    disabled={loading}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-sm btn-danger"
                    onClick={() => openDeleteModal(mark.id)}
                    disabled={loading}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {/* Delete Confirmation Modal */}
      {showDeleteModal && (
        <div
          className="modal fade show d-block"
          tabIndex="-1"
          style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}
        >
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content border-danger">
              <div className="modal-header bg-danger text-white">
                <h5 className="modal-title">Confirm Delete</h5>
                <button
                  type="button"
                  className="btn-close btn-close-white"
                  onClick={() => setShowDeleteModal(false)}
                  disabled={loading}
                ></button>
              </div>
              <div className="modal-body">
                Are you sure you want to delete this mark?
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => setShowDeleteModal(false)}
                  disabled={loading}
                >
                  Cancel
                </button>
                <button
                  type="button"
                  className="btn btn-danger"
                  onClick={confirmDelete}
                  disabled={loading}
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Message Modal */}
      {showMessageModal && (
        <div
          className="modal fade show d-block"
          tabIndex="-1"
          style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}
        >
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content border-primary">
              <div className="modal-body text-center">
                <p>{message}</p>
                <button className="btn btn-primary" onClick={() => setShowMessageModal(false)}>
                  OK
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default MarkManagement;
